#else  // multi threaded server
int main(int ac, char **av)
{
PARAM p;
int   s,ret;

  eib = new rlEIBnetIP(EIB_MAXSIGNALS,1);
  client = new rlIpAdr;
  server = new rlIpAdr;

  pvInit(ac,av,&p);
  client->setAdr(EIB_LOCALHOST,rlEIBnetIP::PORT);
  server->setAdr(EIB_GATEWAY,rlEIBnetIP::PORT);
  ret = eib->setClient(client);
  if(ret < 0) return -1;
  ret = eib->setServer(server);
  if(ret < 0) return -1;
#ifdef DEBUG
  eib->debug = 1;
#endif
  eib->startReading();

  /* here you may interpret ac,av and set p->user to your data */
  while(1)
  {
    s = pvAccept(&p);
    if(s != -1) pvCreateThread(&p,s);
    else        break;
  }
  return 0;
}
#endif
